curl -s -k -H "Authorization: Bearer $TOKEN" -H 'Accept: application/json' https://$ENDPOINT/apis/networking.k8s.io/v1/networkpolicies | jq -r '"Type:" + .items[].spec.policyTypes[0] +", name: " + .items[].metadata.name + ", namespace: " + .items[].metadata.namespace +", From labeled: " + .items[].spec.ingress[].from[].podSelector.matchLabels.app + ", Port: " + (.items[].spec.ingress[].ports[].port|tostring) + ", Protocol:" + .items[].spec.ingress[].ports[].protocol+ ", To labeled: "+ .items[].spec.podSelector.matchLabels.app + "\n"' | sort -u
